#ifndef AVATARADLLEXPORTS_H_
#define AVATARADLLEXPORTS_H_

#ifdef WIN32
	#ifdef AVATARA_EXPORTS
	#define AVATARA_API __declspec(dllexport)
	#else
	#define AVATARA_API __declspec(dllimport)
	#endif
#else
	#define AVATARA_API
#endif


#endif /* AVATARADLLEXPORTS_H_ */
